/**
 * Get all available shaders with their metadata
 * @returns Array of shader metadata objects
 */
export function getAllShaders() {
  return shaderRegistry
}

/**
 * Get a specific shader by name
 * @param name - The shader name (e.g., 'LinearGradient')
 * @returns Shader metadata or undefined if not found
 */
export function getShaderByName(name) {
  return shaderRegistry.find(shader => shader.name === name)
}

/**
 * Get all shaders in a specific category
 * @param category - The category name (e.g., 'Blurs')
 * @returns Array of shaders in the category
 */
export function getShadersByCategory(category) {
  return shaderRegistry.filter(shader => shader.category === category)
}

/**
 * Get all unique categories
 * @returns Array of category names
 */
export function getCategories() {
  return [...new Set(shaderRegistry.map(shader => shader.category))]
}
